//Проведение документа
function activate(currentDocument) {
    if (notValid(currentDocument)) {
        return notValid(currentDocument);
    }
    
    let summ = 0;
    let acc30304 = EntityFactory.getPlan().getAccounts().findByCode('30304').get(0);
    let acc21011 = EntityFactory.getPlan().getAccounts().findByCode('21011').get(0);
    let docMnemo = 'Kassa.PlatezhnyePorucheniyaspisanieSoScheta';
    
    for (let tableRec of currentDocument.getRows()){

        currentDocument.addPosting(createDefaultPostingByTableRec(tableRec, docMnemo));

        if (tableRec.get(docMnemo + '.tr.SummaNDS'))
            currentDocument.addPosting(createPostingByTableRec(tableRec, docMnemo, acc30304, acc21011, docMnemo + '.tr.SummaNDS'));

        summ += +tableRec.get(docMnemo + '.tr.Summa');            
    };

    currentDocument.setExecuted();
    currentDocument.setOperationSum(summ);
    currentDocument.save();
}

function deactivate(currentDocument) {
    currentDocument.unPostingAndSave();
}
