function preSave(currentDocument) {
    processDocument(currentDocument, 
        new DocNdsHandler(),
        new DocSummaHandler(), 
        new DocReferenceHandler('TMC', ['EdIzm', 'Strana'], ['EdinicaIzmereniya', 'Tovaroproizvoditel']), 
        new DocFillTableByHatHandler('Operaciya', 'Operaciya'), 
        new DocReferenceHandler('Operaciya', ['KVFO', 'KPSDebet', 'Debet', 'KPSKredit', 'Kredit'], ['KVFO', 'KPSDb', 'Debet', 'KPSKr', 'Kredit'])
    );
}

//то же самое(за исключением НДС и заполнения дб, кр и тд из операции) - как пример простого, понятного решения
function preSaveSimpleForUnderstanding(currentDocument) { 
    let mnemo = currentDocument.getMnemo(); 
    currentDocument.set(mnemo + '.hr.Summa', getCalculatedSum(currentDocument, mnemo + '.tr.VsegoSNDS'));
    
    let operation = currentDocument.get(mnemo + '.hr.Operaciya');
    for (let tableRec of currentDocument.getRows()){
        if (operation && !tableRec.get(mnemo + '.tr.Operaciya')){
            tableRec.put(mnemo + '.tr.Operaciya', operation);
        }
        
        let tmc = tableRec.get(mnemo + '.tr.' + 'TMC');
        if (tmc) {
            tableRec.put(mnemo + '.tr.' + 'EdinicaIzmereniya', tmc.get(tmc.getMnemo() + '.' + 'EdIzm'));
            tableRec.put(mnemo + '.tr.' + 'Tovaroproizvoditel', tmc.get(tmc.getMnemo() + '.' + 'Strana'));
        }        
    }
}

function baseOn(base, targetMnemo){
    let baseMnemo = base.getMnemo();
    let currentDocument = EntityFactory.createDocument(targetMnemo);
    let docMnemo = currentDocument.getMnemo();

    currentDocument.setParent(base);
    currentDocument.setNumber('');  
    currentDocument.setOrganization(base.getOrganization()); 
    
    switch (baseMnemo) {
        case MODULEMNEMO + '.ZayavkiNaKassovyiiRashod':
        case MODULEMNEMO + '.PlatezhnyePorucheniyaspisanieSoScheta':
            /* Заявки на кассовый расход / Платежные поручения 
            Из шапки в шапку Акта приемки работ
                Сумма -> Сумма, Получатель -> Поставщик
            Из фактуры ЗКР в фактуру Акта
                КВФО -> КВФО, Дебет+КПСдб+Аналитика -> Кредит+КПСкр+Аналитика, Сумма без НДС -> Сумма без НДС, НДС% -> НДС%, Сумма НДС -> Сумма НДС, Сумма -> Всего с НДС */   
            currentDocument.set(docMnemo + '.hr.Summa', base.get(baseMnemo + '.hr.Summa'));
            currentDocument.set(docMnemo + '.hr.Postavshik', base.get(baseMnemo + '.hr.Poluchatel'));

            for (let tableRec of base.getRows()){
                let row =  currentDocument.addTableRow();

                row.put(docMnemo + '.tr.KVFO', tableRec.get(baseMnemo + '.tr.KVFO'));
                row.put(docMnemo + '.tr.KPSKr', tableRec.get(baseMnemo + '.tr.KPSDb'));
                row.put(docMnemo + '.tr.Kredit', tableRec.get(baseMnemo + '.tr.Debet'));
                row.put(docMnemo + '.tr.SummaBezNDS', tableRec.get(baseMnemo + '.tr.SummaBezNDS'));
                row.put(docMnemo + '.tr.NDS', tableRec.get(baseMnemo + '.tr.NDS'));
                row.put(docMnemo + '.tr.SummaNDS', tableRec.get(baseMnemo + '.tr.SummaNDS'));
                row.put(docMnemo + '.tr.VsegoSNDS', tableRec.get(baseMnemo + '.tr.Summa'));
            }
            break;
        case MODULEMNEMO + '.SchetafakturyPoluchennye':
            /* Из шапки Счета-фактуры полученного в шапку Акта приемки работ (услуг)
               Сумма -> Сумма, Продавец -> Поставщик, Подразделение -> Подразделение, Назначение платежа -> Примечание
            */
            copyFieldValuesHr(base, 
                              currentDocument, 
                              ['Summa', ['Postavshik', 'Prodavec'], 'Podrazdelenie', ['Primechanie', 'NaznacheniePlatezha']]);             
            
            /* Из шапки в фактуру 
               Балансовый счет -> Кредит, 
               аналитика 1: Контрагенты -> аналитика 1: Контрагенты, 
               аналитика 2: Счета-фактуры полученные -> аналитика 2: Счета-фактуры  полученные, 
               аналитика 3: Договоры, гос.контракты -> аналитика 3: Договоры, гос.контракты (аналитики Кредита)  
            */

            let acc = base.get(baseMnemo + '.hr.BalansovyiiSchet');
 
            /* 
               Из фактуры в фактуру
               ТМЦ -> ТМЦ, Единица измерения -> Единица измерения, Количество -> Количество, 
               Товаропроизводитель -> Товаропроизводитель, № ГТД -> № ГТД, Акциз в т.ч. -> Акциз в т.ч., 
               Сумма без НДС -> Сумма без НДС, НДС% -> НДС%, Сумма НДС -> Сумма НДС, Всего с НДС -> Всего с НДС
            */ 
            
            for (let tableRec of base.getRows()){
                let row =  currentDocument.addTableRow();
                
                copyFieldValuesTr(tableRec, row, baseMnemo, docMnemo,
                    ['TMC', 'EdinicaIzmereniya', 'Kolichestvo', 'Tovaroproizvoditel', ['NGTD', 'GTD'], ['AkcizVTch', 'AkcizVTCH'], 'SummaBezNDS', 'NDS', 'SummaNDS', 'VsegoSNDS']);
                
                row.put(docMnemo + '.tr.Kredit', acc);
                row.put(docMnemo + '.tr.EdinicaIzmereniya', tableRec.get(baseMnemo + '.tr.EdinicaIzmereniya'));
                row.put(docMnemo + '.tr.Tovaroproizvoditel', tableRec.get(baseMnemo + '.tr.Tovaroproizvoditel'));                                
            }     
            
            break;
    }
    currentDocument.save()
    return currentDocument;
}
