//Проведение документа
function activate(currentDocument) {
    const DOCMNEMO = 'NakladnyeNaVnutreneePeremeshenieOS';
    let resultValidate = currentDocument.validate();
    
    if (notValid(currentDocument)) {
        return notValid(currentDocument);
    };            
    
    let summ = 0;
    let KreditZero = null;
    let KreditZeroKosgu;
         
    if (EntityFactory.getPlan().getAccounts().findByCode('00000').size() > 0) {
        KreditZero = EntityFactory.getPlan().getAccounts().findByCode('00000').get(0);                         
        KreditZeroKosgu = getKOSGUCredit(KreditZero);
    }    
    
    for (let tableRec of currentDocument.getRows()){
         let summs = tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.Stoimost'); 
         let summAmo = tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.AmortizaciyaDo1');
         
         let Debet;         
         let Kredit;
         let DebetKosgu;
         let KreditKosgu;
         let KartDt;
         let SubKartDt;
         let Ekr;
         
         
         //---ПО 1 ПРОВОДКЕ
         let postingProps = {
              tableRec,
              currentDocument,
              docMnemo: MODULEMNEMO + '.' + DOCMNEMO,
              summa: summs,
         }
                  
         //добавление проводки 1         
         currentDocument.addPosting(createPostingWithProps(postingProps));
         
         
         //-------------------------------------------------------------------------
         // по счету Дебет ищем счет затрат в Соответствии счетов (для 2-3 проводки)
         let Dt = tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.Debet');
         
         if (Dt != null) {              
             //Если в справочнике Соответствие счетов найден нужный счет, то...  
             Debet = null;
             
             if (EntityFactory.getCatalogs().get(MODULEMNEMO + ".SootvetstviyaSchetov").findByCode(Dt.getCode()).size() > 0) {    
                 KartDt = EntityFactory.getCatalogs().get(MODULEMNEMO + ".SootvetstviyaSchetov").findByCode(Dt.getCode());
                 SubKartDt = KartDt.get(0);
                 
                 if (EntityFactory.getCatalogs().get(MODULEMNEMO + ".KOSGUEKR").findByCode("271").size() > 0) {    
                     Ekr = EntityFactory.getCatalogs().get(MODULEMNEMO + ".KOSGUEKR").findByCode("271").get(0);
                 }
                
                 if (SubKartDt.get(MODULEMNEMO + ".SootvetstviyaSchetov.SchetZatrat") != null) {
                     let AccZatrat = SubKartDt.get(MODULEMNEMO + ".SootvetstviyaSchetov.SchetZatrat").getCode(); 
                     
                     if (EntityFactory.getPlan().getAccounts().findByCode(AccZatrat).size() > 0) {
                         Debet = EntityFactory.getPlan().getAccounts().findByCode(AccZatrat).get(0);                         
                         DebetKosgu = getKOSGUDebit(Debet);
                         Debet = SetAnaliticInAccount(Debet, 'KOSGUEKR', Ekr);
                     }    
                 }    
             }  
         }    
         //ПО 2 ПРОВОДКЕ
         if (summAmo > 0) {            
             // по счету Кредит ищем счет амортизации в Соответствии счетов
             Kredit = null;
             
             if (EntityFactory.getCatalogs().get(MODULEMNEMO + ".SootvetstviyaSchetov").findByCode(Dt.getCode()).size() > 0) { 
                 if (SubKartDt.get(MODULEMNEMO + ".SootvetstviyaSchetov.SchetAmortizacii") != null) {
                     let AccAmo = SubKartDt.get(MODULEMNEMO + ".SootvetstviyaSchetov.SchetAmortizacii").getCode();                       
                     
                     if (EntityFactory.getPlan().getAccounts().findByCode(AccAmo).size() > 0 ) {
                         Kredit = EntityFactory.getPlan().getAccounts().findByCode(AccAmo).get(0);                         
                         KreditKosgu = getKOSGUCredit(Kredit);
                         Kredit = fillAccountByAnalytics(Kredit, Dt);
                     }    
                     
                 }    
             
             }
             
             //добавление проводки 2
             let postingProps2 = {
                 tableRec,
                 currentDocument,
                 docMnemo: MODULEMNEMO + '.' + DOCMNEMO,
                 debitKps: tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.KPSKr').getCode(), 
                 debitKosgu: DebetKosgu,
                 debit: Debet,
                 creditKps: tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.KPSDb').getCode(),
                 creditKosgu: KreditKosgu,
                 credit: Kredit,
                 summa: summAmo,
             }
            
             //добавление проводки 2         
             currentDocument.addPosting(createPostingWithProps(postingProps2));
         }; 

   
        //------------------------
        //ПО 3 ПРОВОДКЕ
        //Читаем код ОС в фактуре
        let DataDict;
        let MinSummDict=0;  
        
        //Если выбрано ОС
        if (tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.OS') != null) {
            let CodeOS = tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.OS').getCode();
            
            //если ОС найдено в картотеке
            if (EntityFactory.getCatalogs().get(MODULEMNEMO + ".KartotekaOS").findByCode(CodeOS).size() > 0) {
                //Получаем карточку из ОС картотеки
                let KartOS = EntityFactory.getCatalogs().get(MODULEMNEMO + ".KartotekaOS").findByCode(CodeOS).get(0);              
                
                //Получаем Дату прихода и Дату ввода в эксплуатацию
                //затем, выбираем наименьшую из них
                let DataPrih = KartOS.get(MODULEMNEMO + ".KartotekaOS.DataPrihoda");
                let DataVvoda
                let MinDt
                
                //Если дата ввода в фактуре не заполнена, то берем из картотеки, иначе из фактуры...
                if (tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.DataVvodaVEkspluataciiu') == null) {
                    DataVvoda = KartOS.get(MODULEMNEMO + ".KartotekaOS.DataVvodaVEkspluataciiu");
                } else {
                    DataVvoda = tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.DataVvodaVEkspluataciiu');
                };  
        
                //Берем наименьшее из даты ввода и даты прихода
                if (DataPrih != null && DataVvoda != null) {
                    MinDt = Math.min(DataPrih, DataVvoda);  
                } else if (DataPrih != null) {
                    MinDt = DataPrih;
                } else  if (DataVvoda != null) {
                    MinDt = DataVvoda;
                };                    
               
                let i = 1;
                
                if (MinDt != null) {
                    //Получаем справочник "Границы стоимости ОС"
                    let DictBordersOfCost = EntityFactory.getCatalogs().get(MODULEMNEMO + '.GranicyStoimostiOSDlya1Amortizacii');           
                    let catalog;
                                        
                    //Идем по справочнику
                    //Сравниваем мин. дату с датой из справочника
                    //Если дата из справочника меньше мин. даты, то -> если дата из справочника  
                    DictBordersOfCost.forEach((catalog) => {
                       let data = catalog.get(MODULEMNEMO + '.GranicyStoimostiOSDlya1Amortizacii.Data');
                       if (i == 1) {
                           DataDict = data; 
                       }

                       if (MinDt >= data) {
                           if (data > DataDict)  {
                               DataDict = data;
                               MinSummDict = catalog.get(MODULEMNEMO + '.GranicyStoimostiOSDlya1Amortizacii.MinimalnayaGranica');
                         }                          
                       } 
                       i += +1;                       
                    });                       
                }                
                //resultValidate.addValueError(DataDict + '---' + MinSummDict + '----' + i); 
            };    
        };    
        
        //если стоимость меньше минимальной границы для 100%
        if (summs < MinSummDict) {
            let Kr = tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.Debet');                       
            Kredit = null;
            
            if (EntityFactory.getPlan().getAccounts().findByCode(Kr.getCode()).size() > 0 ) {
                Kredit = EntityFactory.getPlan().getAccounts().findByCode(Kr.getCode()).get(0);                         
                KreditKosgu = getKOSGUCredit(Kredit);
                Kredit = fillAccountByAnalytics(Kredit, Dt);
            }             
            
            //добавление проводки 3
            let postingProps3 = {
                tableRec,
                currentDocument,
                docMnemo: MODULEMNEMO + '.' + DOCMNEMO,
                debitKps: tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.KPSKr').getCode(), 
                debitKosgu: DebetKosgu,
                debit: Debet,
                creditKps: tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.KPSDb').getCode(),
                creditKosgu: KreditKosgu,
                credit: Kredit,
                summa: summs,
            }
            
            //добавление проводки 3         
            currentDocument.addPosting(createPostingWithProps(postingProps3));
             
            //ПО 4 ПРОВОДКЕ
            //добавление проводки 4
            if (SubKartDt.get(MODULEMNEMO + ".SootvetstviyaSchetov.SchetZabalansovyii") != null) {
                let AccZabal = SubKartDt.get(MODULEMNEMO + ".SootvetstviyaSchetov.SchetZabalansovyii").getCode(); 
                Debet = null; 
                
                if (EntityFactory.getPlan().getAccounts().findByCode(AccZabal).size() > 0) {
                    Debet = EntityFactory.getPlan().getAccounts().findByCode(AccZabal).get(0);                         
                    DebetKosgu = getKOSGUDebit(Debet);
                    Debet = fillAccountByAnalytics(Debet, Dt);
                }    
            }             
            
            let postingProps4 = {
                tableRec,
                currentDocument,
                docMnemo: MODULEMNEMO + '.' + DOCMNEMO,
                debitKps: tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.KPSKr').getCode(), 
                debitKosgu: DebetKosgu,
                debit: Debet,
                creditKps: tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.KPSDb').getCode(),
                creditKosgu: KreditZeroKosgu,
                credit: KreditZero,
                summa: summs,
             }
            
             //добавление проводки 4         
             currentDocument.addPosting(createPostingWithProps(postingProps4));             
        }
        
        //ПО 5 ПРОВОДКЕ
        //если второй счет, забаланс - не пустой...
        if (tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.VtoroiiSchetZabalans') != null) {
            //resultValidate.addValueError('---');
            //return  resultValidate;              
            let AccSecZabal = tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.VtoroiiSchetZabalans').getCode(); 
            Debet = null;
            
            if (EntityFactory.getPlan().getAccounts().findByCode(AccSecZabal).size() > 0) {
                Debet = EntityFactory.getPlan().getAccounts().findByCode(AccSecZabal).get(0);                         
                DebetKosgu = getKOSGUDebit(Debet);
                Debet = fillAccountByAnalytics(Debet, Dt);
            }    
            
            let postingProps5 = {
                tableRec,
                currentDocument,
                docMnemo: MODULEMNEMO + '.' + DOCMNEMO,
                debitKps: tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.KPSKr').getCode(), 
                debitKosgu: DebetKosgu,
                debit: Debet,
                creditKps: tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.KPSDb').getCode(),
                creditKosgu: KreditZeroKosgu,
                credit: KreditZero,
                summa: summs,
            }
            
            //добавление проводки 5         
            currentDocument.addPosting(createPostingWithProps(postingProps5));            
        }
                
        //Вычисление общей суммы операции
        summ += +tableRec.get(MODULEMNEMO + '.' + DOCMNEMO + '.tr.Stoimost');            
    };
        
    //устанавливаем текущую дату проводки
    currentDocument.setExecuted();
    //устанавливаем сумму операции
    currentDocument.setOperationSum(summ);

    //сохранение документа с проводками
    currentDocument.save();
    //   return  resultValidate;
     
    //resultValidate.addValueError(Ekr.getCode());
    //return  resultValidate;       
    return;  
}
