//После проведения документа - пишем в регистр
function postActivate(currentDocument) { 
    let docMnemo = currentDocument.getMnemo();
    let regName = MODULEMNEMO + '.OstatkiTMC';
    let partia;
    let mol;
    let skl;
    let sh;
    
    if (currentDocument.isActivated())  {    

        //ищем в справочнике подменные нулевые значения
        let zeroPartia = EntityFactory.getCatalogs().get(MODULEMNEMO + '.Partii').findByCode('0')[0]; 
        let zeroMol = EntityFactory.getCatalogs().get(MODULEMNEMO + '.Sotrudniki').findByCode('0')[0];
        let zeroSkl = EntityFactory.getCatalogs().get(MODULEMNEMO + '.Podrazdeleniya').findByCode('0')[0];  
    
        for (let tableRec of currentDocument.getRows()){
            sh = tableRec.get(docMnemo + '.tr.Debet');
            //получаем значения аналитик дебета
            partia = getValOrZeroVal(sh, 'Partii', zeroPartia);
            mol = getValOrZeroVal(sh, 'Sotrudniki', zeroMol);
            skl = getValOrZeroVal(sh, 'Podrazdeleniya', zeroSkl);
        
            //заполняем регистр по дебету
            FillRegistr(regName, currentDocument, ['Schet', 'MOL', 'Sklad', 'Partiya', 'TMC'], 
                                                  [sh, mol, skl, partia, tableRec.get(docMnemo + '.tr.TMC')], 
                                                  ['VidDvizheniya'], 
                                                  [docMnemo], 
                                                  ['Kolichestvo', 'Summa'], 
                                                  [tableRec.get(docMnemo + '.tr.Kolichestvo'), tableRec.get(docMnemo + '.tr.Summa')]);
        
            sh = tableRec.get(docMnemo + '.tr.Kredit');
            //получаем значения аналитик Кредита
            partia = getValOrZeroVal(sh, 'Partii', zeroPartia);
            mol = getValOrZeroVal(sh, 'Sotrudniki', zeroMol);
            skl = getValOrZeroVal(sh, 'Podrazdeleniya', zeroSkl);  
          
            //заполняем регистр по дебету
            FillRegistr(regName, currentDocument, ['Schet', 'MOL', 'Sklad', 'Partiya', 'TMC'], 
                                                  [sh, mol, skl, partia, tableRec.get(docMnemo + '.tr.TMC')], 
                                                  ['VidDvizheniya'], 
                                                  [docMnemo], 
                                                  ['Kolichestvo', 'Summa'], 
                                                  [-tableRec.get(docMnemo + '.tr.Kolichestvo') , -tableRec.get(docMnemo + '.tr.Summa')]);
        }  
    };
}

//После снятия с проведения документа - удаляем из регистра
function postDeactivate(currentDocument) {
    //Удаляем регистры при снятии с проведения
    let docMnemo = currentDocument.getMnemo();
    let regName = MODULEMNEMO + '.OstatkiTMC';
    
    let sh_cap = regName + '.dm.Schet';
    let mol_cap = regName + '.dm.MOL';
    let skl_cap = regName + '.dm.Sklad';
    let part_cap = regName + '.dm.Partiya';
    let tmc_cap = regName + '.dm.TMC';   
            
    let sh;
    let mol;
    let skl;
    let part;
    let tmc;
    
    req1 = regName + '.rg.VidDvizheniya';
    
    let condition;
    let myReg;
    
    //ищем в справочнике подменные нулевые значения
    let zeroPartia = EntityFactory.getCatalogs().get(MODULEMNEMO + '.Partii').findByCode('0')[0]; 
    let zeroMol = EntityFactory.getCatalogs().get(MODULEMNEMO + '.Sotrudniki').findByCode('0')[0];
    let zeroSkl = EntityFactory.getCatalogs().get(MODULEMNEMO + '.Podrazdeleniya').findByCode('0')[0];
    
    for (let tableRec of currentDocument.getRows()){
         sh = tableRec.get(docMnemo + '.tr.Debet');
         //получаем значения из аналитики дебета         
         part = getValOrZeroVal(sh, 'Partii', zeroPartia);
         mol = getValOrZeroVal(sh, 'Sotrudniki', zeroMol);
         skl = getValOrZeroVal(sh, 'Podrazdeleniya', zeroSkl);        
        
         condition = new filter().dimension(sh_cap, sh).and().dimension(mol_cap, mol).and().dimension(skl_cap, skl).and().dimension(part_cap, part).and().dimension(tmc_cap, tmc).and().requisite(req1, docMnemo);
         
         myReg = Register.get(regName);
         myReg.delete(condition);   
         
         sh = tableRec.get(docMnemo + '.tr.Kredit');
         //получаем партию из аналитики кредита         
         part = getValOrZeroVal(sh, 'Partii', zeroPartia);
         mol = getValOrZeroVal(sh, 'Sotrudniki', zeroMol);
         skl = getValOrZeroVal(sh, 'Podrazdeleniya', zeroSkl);         
        
         condition = new filter().dimension(sh_cap, sh).and().dimension(mol_cap, mol).and().dimension(skl_cap, skl).and().dimension(part_cap, part).and().dimension(tmc_cap, tmc).and().requisite(req1, docMnemo);
         
         myReg = Register.get(regName);
         myReg.delete(condition);          
    }   
}
