//Проведение документа
function activate(currentDocument) {
    //первый первый комит
    //проверка документа
    let validationResult = notValid(currentDocument);
    if (validationResult) {
        return validationResult;
    }
    let docMnemo = currentDocument.getMnemo();
    for (let tableRec of currentDocument.getRows()){
        //добавление проводки
        currentDocument.addPosting(createPostingWithProps({tableRec, docMnemo}));
    }
    //проверка проводок
    let postingValidationResult = hasNotValidPostings(currentDocument);
    if (postingValidationResult) {
        return postingValidationResult;
    }
    //устанавливаем признак проведения и текущую дату
    currentDocument.setExecuted();
    //устанавливаем сумму операции
    currentDocument.setOperationSum(getCalculatedSum(currentDocument, docMnemo + '.tr.Summa'));
    //сохранение документа с проводками
    currentDocument.save();
}
