//Проведение документа
function activate(currentDocument) {
    
    let myReg = Register.get("TestB.RegistrTest");

    //СОЗДАНИЕ ЗАПИСИ РЕГИСТРА
    let record = myReg.create();
    record.setDate(currentDocument.getDate());
    record.setDocument(currentDocument);
    record.setDimension('TestB.RegistrTest.dm.Sklad', currentDocument.get('TestB.Testovyii.hr.Sklad').getTitle());
    record.setDimension('TestB.RegistrTest.dm.Mebel', currentDocument.get('TestB.Testovyii.hr.Mebel').getTitle());
    record.setRequisite('TestB.RegistrTest.rg.VidDvizheniya', currentDocument.get('TestB.Testovyii.hr.VidDvizheniyaTMC'));
    record.setRequisite('TestB.RegistrTest.rg.Rekvizit2', currentDocument.get('TestB.Testovyii.hr.Rekvizit2'));
    record.create();

}
