//Проведение документа 
function activate(currentDocument) {
    //проверка документа
    let validationResult = notValid(currentDocument);
    if (validationResult) {
        return validationResult;
    }
    let docMnemo = currentDocument.getMnemo();
    for (let tableRec of currentDocument.getRows()){
        
        //добавление проводки
        currentDocument.addPosting(createPostingWithProps({
            tableRec, 
            currentDocument,
            //debit: EntityFactory.getPlan().getAccounts().findByCode('00003').get(0),
            //debit: tableRec.get('TestovoePrilozhenie.BuhgalterskieSpravki.tr.Kredit');
            //debit: currentDocument.get('TestovoePrilozhenie.BuhgalterskieSpravki.hr.Schet');
        }));
    }
    //проверка проводок
    let postingValidationResult = hasNotValidPostings(currentDocument);
    if (postingValidationResult) {
        return postingValidationResult;
    }
    
    //устанавливаем признак проведения и текущую дату
    currentDocument.setExecuted();
    //устанавливаем сумму операции
    currentDocument.setOperationSum(getCalculatedSum(currentDocument, docMnemo + '.tr.Summa'));
    //сохранение документа с проводками
    currentDocument.save();
}

//Проверка документа на полное заполнение
function notValid2(currentDocument) { 
    let resultValidate = currentDocument.validate();
    
    if (!currentDocument.getRows().size()){
        resultValidate.addValueError('Не заполнена табличная часть документа.');
    }
    
    if (resultValidate.hasErrors() || resultValidate.hasValueErrors() ) 
        return  resultValidate;
}
