//Проведение документа
function activate(currentDocument) {
   
        let entriesNumber = currentDocument.get('stresstesting.RandomActivation.hr.EntriesNumber');
        let account = EntityFactory.getPlan().getAccounts().findByCode("1").get(0);
        
      

        for(let i=0; i<entriesNumber; i++) {
                let posting = EntityFactory.createPosting();
                posting.setComment( "Случайная проводка" );
                posting.setValue( "100" );
                posting.setDebit( account );
                posting.setCredit( account );
                currentDocument.addPosting(posting);
            }
                
        
        
        currentDocument.setExecuted();
        currentDocument.save();
    
        return;  
}

//Снятие документа с проведения
function deactivate(currentDocument) {
    currentDocument.removeAllPostings();
    currentDocument.setExecutedDate(null);
    currentDocument.save();
    return;
}
