//Проведение документа
function activate(currentDocument) {
    if (!notValid(currentDocument)) {
        let summ = 0;
        
        for (let tableRec of currentDocument.getRows()){
            //создание и заполнение проводки
            let posting = EntityFactory.createPosting();
          
            //КВФО
            posting.setProperty(KFO, tableRec.get('tap.BuhgalterskieSpravki.tr.KVFO')[0]);
            //Дебет
            posting.setDebit(tableRec.get('tap.BuhgalterskieSpravki.tr.Debet'));
            posting.setDebitProperty(KPS, tableRec.get('tap.BuhgalterskieSpravki.tr.KPSDb').getCode());
            posting.setDebitProperty(KOSGU, tableRec.get('tap.BuhgalterskieSpravki.tr.KOSGUDb').getCode());
            //Кредит
            posting.setCredit(tableRec.get('tap.BuhgalterskieSpravki.tr.Kredit'));
            posting.setCreditProperty(KPS, tableRec.get('tap.BuhgalterskieSpravki.tr.KPSKr').getCode());
            posting.setCreditProperty(KOSGU, tableRec.get('tap.BuhgalterskieSpravki.tr.KOSGUKr').getCode());
            //Сумма
            posting.setValue(tableRec.get('tap.BuhgalterskieSpravki.tr.Summa'));
            //Комментарий
            if (tableRec.get('tap.BuhgalterskieSpravki.tr.Operaciya'))
                posting.setComment(tableRec.get('tap.BuhgalterskieSpravki.tr.Operaciya').get('tap.Operacii.PolnoeNaimenovanie'));
            //Расшифровка операции
            if (tableRec.get('tap.BuhgalterskieSpravki.tr.RasshifrovkaOperacii'))
                posting.setProperty(RASOPER, tableRec.get('tap.BuhgalterskieSpravki.tr.RasshifrovkaOperacii'));
            //Количесвто
            if (tableRec.get('tap.BuhgalterskieSpravki.tr.Kolichestvo'))
                posting.setProperty(KOLVO, tableRec.get('tap.BuhgalterskieSpravki.tr.Kolichestvo'));
            
            //Цена
            if (tableRec.get('tap.BuhgalterskieSpravki.tr.Cena'))
                posting.setProperty(CENA,tableRec.get('tap.BuhgalterskieSpravki.tr.Cena'));
          
            currentDocument.addPosting(posting);
            
            //Вычисление общей суммы операции
            summ += +tableRec.get('tap.BuhgalterskieSpravki.tr.Summa');            
        };
        
        //устанавливаем текущую дату проводки
        currentDocument.setExecuted();
        //устанавливаем сумму операции
        currentDocument.setOperationSum(summ);

        //сохранение документа с проводками
        currentDocument.save();
        return;  
    } else 
        return notValid(currentDocument);
}

function deactivate(currentDocument) {
    currentDocument.unPostingAndSave();
}

//Проверка документа на полное заполнение
function notValid(currentDocument) { 
    let resultValidate = currentDocument.validate();
 
    if (!currentDocument.getRows().size()){
        resultValidate.addValueError('Не заполнена табличная часть документа.');
    }
    
    if (resultValidate.hasErrors() || resultValidate.hasValueErrors() ) 
        return  resultValidate;
}
