//Поступление средств в возмещение ущерба и иных доходов (209)
function activate(currentDocument) {

    if (!notValid(currentDocument)) {

        if(currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.Oplacheno')){
            
            let korSchet = currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.KorSchet');
            let korSchetSynthetic = korSchet.getCode().substr(18,3);
            
            for (let tableRec of currentDocument.getRows()){
                let posDebit = posCredit = '';
                let posValue = tableRec.get('tap.PrihodnyeKassovyeOrdera.tr.Summa');
                let posComment = currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.Osnovanie');
                let kfo = tableRec.get('tap.PrihodnyeKassovyeOrdera.tr.KFO');
                let needRazdLicSch = currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.ProvodkiPlanaDohodov') && ((kfo != null) && (kfo.getCode() != 1));

                if (currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.Oplacheno')){

//              Поступление денежных средств
                    posDebit = currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.SchetDt');
                    posCredit = tableRec.get('tap.PrihodnyeKassovyeOrdera.tr.SchetRaschetov');
                    createPosting();
                    posCredit = '';

                    if ( needRazdLicSch ){
//              Получено финансового обеспечения
                        posDebit = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000050810000').get(0);
                        posCredit = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000050710000').get(0);
                        posCredit.put(0, currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.RazdelLicevyhSchetov')); //Аналитика 1: Разделы лиц счетов
                        createPosting();
                        posCredit = '';
                    }

//              Забалансовая проводка
                    posDebit = EntityFactory.getPlan().getAccounts().findByCode('0000000000000000000НД00000').get(0);
                    createPosting();

                    if (currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.VozvratProshlyhLet')){
                        if (kfo != null){
                            if (kfo.getCode() != 1){
//              Проводка по счету 17 (возврат прошлых лет, БУ и АУ)
                                posDebit = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000001734000').get(0);
                                createPosting();
                            } else {
//              Проводка по счету 18 (возврат прошлых лет, КУ)
                                posDebit = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000001834000').get(0);
                                createPosting();
                            }
                        }
//              Возврат прошлых лет
                        posDebit = EntityFactory.getPlan().getAccounts().findByCode('000000000000000000ВПЛ34000').get(0);
                        posComment = 'Возврат прошлых лет';
                        createPosting();
                        posComment = currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.Osnovanie');
                    } else {
//              Проводка по счету 17
                        posDebit = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000001734000').get(0);
                        createPosting();
                    }

                }

//          Общая функция создания и заполнения проводки
                function createPosting() {
                    let posting = EntityFactory.createPosting();
                    if (posDebit)
                        posting.setDebit(posDebit);
                    if (posCredit)
                        posting.setCredit(posCredit);
                    if (posValue)
                        posting.setValue(posValue);
                    if (posComment)
                        posting.setComment(posComment);
                    currentDocument.addPosting(posting);
                    return;
                }
            }
        }    
    
        currentDocument.setExecuted();
        currentDocument.save();
        
        return;  
    
    } else 
        return notValid(currentDocument);
}

//Снятие документа с проведения
function deactivate(currentDocument) {
    currentDocument.removeAllPostings();
    currentDocument.setExecutedDate(null);
    currentDocument.save();
    return;
}

//Проверка документа на полное заполнение
function notValid(currentDocument) {
    let resultValidate = currentDocument.validate();

    if (!currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.Oplacheno'))
            resultValidate.addValueError('tap.PrihodnyeKassovyeOrdera.hr.Oplacheno', 'Для проведения ордер должен быть оплачен.');
        
    if (currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.SchetDt') &&
        currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.SchetDt').getCode().substr(18,5) != '20134')
            resultValidate.addValueError('Счет Дт должен принимать значение 201.34');
                    
    if (currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.KorSchet') &&
        currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.KorSchet').getCode().substr(18,3) != '209')
            resultValidate.addValueError('tap.PrihodnyeKassovyeOrdera.hr.KorSchet', 'Счет должен относиться к группе счетов 209.00.');


    if (!currentDocument.getRows().size())
        resultValidate.addValueError('Не заполнена табличная часть документа.');
    else {
        let sum = currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.Summa');
        let sumChek = 0.00;

        for (let tableRec of currentDocument.getRows()){
            
            let kfoCheck = tableRec.get('tap.PrihodnyeKassovyeOrdera.tr.KFO');
            let needRazdLicSchCheck = currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.ProvodkiPlanaDohodov') && ((kfoCheck != null) && (kfoCheck.getCode() != 1)) && !currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.RazdelLicevyhSchetov');
            
            if (!tableRec.get('tap.PrihodnyeKassovyeOrdera.tr.SchetRaschetov'))
                  resultValidate.addValueError('tap.PrihodnyeKassovyeOrdera.tr.SchetRaschetov', 'Не заполнено обязательное поле в табличной части.');
            
            if (needRazdLicSchCheck)
              resultValidate.addValueError('tap.PrihodnyeKassovyeOrdera.hr.RazdelLicevyhSchetov', 'Не заполнено обязательное поле.');

            if (tableRec.get('tap.PrihodnyeKassovyeOrdera.tr.SchetRaschetov') && (tableRec.get('tap.PrihodnyeKassovyeOrdera.tr.SchetRaschetov').getCode() != currentDocument.get('tap.PrihodnyeKassovyeOrdera.hr.KorSchet').getCode()))
                  resultValidate.addValueError('tap.PrihodnyeKassovyeOrdera.tr.SchetRaschetov', 'Должен совпадать со значением поля "Кор. счет".');
            
            sumChek += +tableRec.get('tap.PrihodnyeKassovyeOrdera.tr.Summa');
        }

//      сумма документа = сумме из строк
        if (+sumChek.toFixed(2) != +sum)
            resultValidate.addValueError('tap.PrihodnyeKassovyeOrdera.hr.Summa', 'Сумма документа не совпадает с суммой в табличной части.');
    }
    
    if (resultValidate.hasErrors() || resultValidate.hasValueErrors()) 
        return  resultValidate;
}
