//Проведение документа
function activate(currentDocument) {

    if (!notValid(currentDocument)) {
        for (let tableRec of currentDocument.getRows()){
            
            //создание и заполнение проводок
            
            let KorSchet = currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.KorSchet');
            let KFO =  tableRec.get('tap.RashodnyeKassovyeOrdera.tr.KFO').getCode();
            let VidKPS =  tableRec.get('tap.RashodnyeKassovyeOrdera.tr.KPS').get('tap.KPS.Vid');
            let KorSchetNum = KorSchet.getCode().substr(18,5);
            
            
            //1. Основная проводка
            let posting1 = EntityFactory.createPosting();
            posting1.setDebit(KorSchet);
            posting1.setCredit(currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.SchetKt'));
            posting1.setValue(tableRec.get('tap.RashodnyeKassovyeOrdera.tr.Summa'));
            posting1.setComment(currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.Osnovanie')?.getTitle())
            currentDocument.addPosting(posting1);
          
            //2. Забалансовая проводка
            let posting2 = EntityFactory.createPosting();
            let shetCt2 = EntityFactory.getPlan().getAccounts().findByCode('0000000000000000000НД00000').get(0);
            posting2.setCredit(shetCt2);
            posting2.setValue(tableRec.get('tap.RashodnyeKassovyeOrdera.tr.Summa'));
            posting2.setComment(currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.Osnovanie')?.getTitle());
            currentDocument.addPosting(posting2);

            //3. Проводка по сч. 17.34. БЮДЖЕТ
            if ( KFO === '1' && VidKPS != "КРБ" ){
                let posting3 = EntityFactory.createPosting();
                let shetKt3 = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000001734000').get(0);
                posting3.setCredit(shetKt3);
                posting3.setValue(tableRec.get('tap.RashodnyeKassovyeOrdera.tr.Summa'));
                posting3.setComment(currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.Osnovanie')?.getTitle());
                currentDocument.addPosting(posting3);
            }
            
              //4. Проводка по сч. 18.34. БЮДЖЕТ
            if ( KFO === '1' && VidKPS === "КРБ" ){
                let posting4 = EntityFactory.createPosting();
                let shetKt4 = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000001834000').get(0);
                posting4.setCredit(shetKt4);
                posting4.setValue(tableRec.get('tap.RashodnyeKassovyeOrdera.tr.Summa'));
                posting4.setComment(currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.Osnovanie')?.getTitle());
                currentDocument.addPosting(posting4);
            }
            
              //5. Проводка по сч. 17.34. ВНЕБЮДЖЕТ
            if ( KFO !== '1'){
                let posting5 = EntityFactory.createPosting();
                let shetKt5 = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000001734000').get(0);
                posting5.setCredit(shetKt5);
                posting5.setValue(tableRec.get('tap.RashodnyeKassovyeOrdera.tr.Summa'));
                posting5.setComment(currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.Osnovanie')?.getTitle());
                currentDocument.addPosting(posting5);
            }

            //6. Проводка по сч. 17.30. БЮДЖЕТ
            if ( KFO === '1' && VidKPS != "КРБ" ){
                let posting6 = EntityFactory.createPosting();
                let shetDt6 = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000001730000').get(0);
                posting6.setDebit(shetDt6);
                posting6.setValue(tableRec.get('tap.RashodnyeKassovyeOrdera.tr.Summa'));
                posting6.setComment(currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.Osnovanie')?.getTitle());
                currentDocument.addPosting(posting6);
            }
            
            //7. Проводка по сч. 18.30. БЮДЖЕТ
            if ( KFO === '1' && VidKPS === "КРБ" ){
                let posting7 = EntityFactory.createPosting();
                let shetDt7 = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000001830000').get(0);
                posting7.setDebit(shetDt7);
                posting7.setValue(tableRec.get('tap.RashodnyeKassovyeOrdera.tr.Summa'));
                posting7.setComment(currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.Osnovanie')?.getTitle());
                currentDocument.addPosting(posting7);
            }
            
             //8. Проводка по сч. 17.30. ВНЕБЮДЖЕТ
            if ( KFO !== '1'){
                let posting8 = EntityFactory.createPosting();
                let shetDt8 = EntityFactory.getPlan().getAccounts().findByCode('00000000000000000001730000').get(0);
                posting8.setDebit(shetDt8);
                posting8.setValue(tableRec.get('tap.RashodnyeKassovyeOrdera.tr.Summa'));
                posting8.setComment(currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.Osnovanie')?.getTitle());
                currentDocument.addPosting(posting8);
            }
        };
                
        //устанавливаем текущую дату проводки
        currentDocument.setExecuted();

        //сохранение документа с проводками
        currentDocument.save();

        return; 

    } else return notValid(currentDocument);
        
}

//Снятие документа с проведения
function deactivate(currentDocument) {
    currentDocument.removeAllPostings();
    currentDocument.setExecutedDate(null);
    currentDocument.save();
    return;
}

//Проверка документа на полное заполнение
function notValid(currentDocument) { 
    let resultValidate = currentDocument.validate();
    
     //Документ должен быть оплачен
    if (!currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.Oplacheno'))
        resultValidate.addValueError('tap.RashodnyeKassovyeOrdera.hr.Oplacheno', 'Документ должен быть оплачен');
    
    // Счет кредита должен быть 20134
    if ( currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.SchetKt') &&
        currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.SchetKt').getCode().substr(18,5) !== '20134') 
            resultValidate.addValueError('tap.RashodnyeKassovyeOrdera.hr.SchetKt', 'Счет должен принимать значение 201.34');
    
    //Допустимое значение счета данного алгоритма
    if (currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.KorSchet')){
        let korSchet = currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.KorSchet').getCode().substr(18,5);
        if ( korSchet !== '21003'){
                resultValidate.addValueError('tap.RashodnyeKassovyeOrdera.hr.KorSchet', 'Счет должен принимать значение 210.03');
        }
    } 
    
    //Заполнена табличная часть
    if (!currentDocument.getRows().size())
        resultValidate.addValueError('Не заполнена табличная часть документа.');
    else {

        let tableSum = 0;
        let docSum = +currentDocument.get('tap.RashodnyeKassovyeOrdera.hr.Summa')

        for (let tableRec of currentDocument.getRows()){
            tableSum += +tableRec.get('tap.RashodnyeKassovyeOrdera.tr.Summa');     
        }

        // сумма в шапке ровняется сумме всех строк
        if (tableSum.toFixed(2) !== docSum.toFixed(2))
            resultValidate.addValueError(`Сумма(всего) (${docSum.toFixed(2)}) не совпадает с суммой строк в табличной части (${tableSum.toFixed(2)})`);

    } 
    
    if (resultValidate.hasErrors() || resultValidate.hasValueErrors() ) 
        return  resultValidate;
}
